import { useState, useEffect, useCallback, useReducer } from 'react';
import TodoList from './components/TodoList';
import TodoForm from './components/TodoForm';

const todoReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TODO':
      return [...state, { id: Date.now(), text: action.payload, completed: false }];
    case 'TOGGLE_TODO':
      return state.map(todo => todo.id === action.payload ? {...todo, completed: !todo.completed} : todo);
    case 'DELETE_TODO':
      return state.filter(todo => todo.id !== action.payload);
    case 'EDIT_TODO':
      return state.map(todo => todo.id === action.payload.id ? {...todo, text: action.payload.text} : todo);
    default:
      return state;
  }
};

function App() {
  const [todos, dispatch] = useReducer(todoReducer, []);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    const saved = localStorage.getItem('todos');
    if (saved) {
      JSON.parse(saved).forEach(todo => {
        dispatch({type: 'ADD_TODO', payload: todo.text});
      });
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  return (
    <div style={{maxWidth: '600px', margin: '0 auto', padding: '20px'}}>
      <h1>Todo App</h1>
      <TodoForm dispatch={dispatch} />
      <div style={{marginBottom: '20px'}}>
        <button onClick={() => setFilter('all')} style={{marginRight: '10px'}}>All ({todos.length})</button>
        <button onClick={() => setFilter('active')} style={{marginRight: '10px'}}>Active ({todos.filter(t => !t.completed).length})</button>
        <button onClick={() => setFilter('completed')}>Completed ({todos.filter(t => t.completed).length})</button>
      </div>
      <TodoList todos={filteredTodos} dispatch={dispatch} />
    </div>
  );
}

export default App;

