document.addEventListener('DOMContentLoaded', () => {
  const container = document.querySelector('.carousel-container');
  const slides = Array.from(container.querySelectorAll('img'));
  const prevBtn = document.querySelector('.carousel-prev');
  const nextBtn = document.querySelector('.carousel-next');
  const dotsContainer = document.querySelector('.carousel-dots');

  let currentIndex = 0;
  let autoPlayInterval;
  const totalSlides = slides.length;

  const createDots = () => {
    dotsContainer.innerHTML = '';
    slides.forEach((_, index) => {
      const dot = document.createElement('span');
      dot.addEventListener('click', () => goToSlide(index));
      dotsContainer.appendChild(dot);
    });
  };

  const updateDots = () => {
    document.querySelectorAll('.carousel-dots span').forEach((dot, index) => {
      dot.classList.toggle('active', index === currentIndex);
    });
  };

  const goToSlide = (index) => {
    currentIndex = index;
    container.style.transform = `translateX(-${currentIndex * 100}%)`;
    updateDots();
  };

  const nextSlide = () => goToSlide((currentIndex + 1) % totalSlides);
  const prevSlide = () => goToSlide((currentIndex - 1 + totalSlides) % totalSlides);

  const startAutoPlay = () => {
    autoPlayInterval = setInterval(nextSlide, 4000);
  };

  const pauseAutoPlay = () => clearInterval(autoPlayInterval);

  nextBtn.addEventListener('click', nextSlide);
  prevBtn.addEventListener('click', prevSlide);
  container.addEventListener('mouseenter', pauseAutoPlay);
  container.addEventListener('mouseleave', startAutoPlay);

  document.addEventListener('keydown', (e) => {
    if (e.key === 'ArrowRight') nextSlide();
    if (e.key === 'ArrowLeft') prevSlide();
  });

  createDots();
  updateDots();
  startAutoPlay();
});

