import { useEffect, useRef, useCallback } from 'react';
import { createPortal } from 'react-dom';

const Modal = ({ isOpen, onClose, children, size = 'md', title }) => {
  const overlayRef = useRef();
  const contentRef = useRef();

  const handleKeyDown = useCallback((e) => {
    if (e.key === 'Escape') {
      e.preventDefault();
      onClose();
    }
  }, [onClose]);

  useEffect(() => {
    if (!isOpen) return;

    const overlay = overlayRef.current;
    overlay.addEventListener('click', onClose);
    overlay.addEventListener('keydown', handleKeyDown);

    return () => {
      overlay.removeEventListener('click', onClose);
      overlay.removeEventListener('keydown', handleKeyDown);
    };
  }, [isOpen, onClose, handleKeyDown]);

  if (!isOpen) return null;

  const sizeClasses = {
    sm: '384px',
    md: '448px',
    lg: '512px',
  };

  return createPortal(
    <div style={{position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000}} ref={overlayRef}>
      <div style={{background: 'white', borderRadius: '8px', maxWidth: sizeClasses[size], maxHeight: '90vh', overflow: 'auto'}} ref={contentRef} role="dialog" aria-modal="true">
        {title && <div style={{padding: '24px', borderBottom: '1px solid #e5e7eb', display: 'flex', justifyContent: 'space-between'}}>
          <h2>{title}</h2>
          <button onClick={onClose} style={{background: 'none', border: 'none', fontSize: '24px', cursor: 'pointer'}}>✕</button>
        </div>}
        <div style={{padding: '24px'}}>{children}</div>
      </div>
    </div>,
    document.body
  );
};

export default Modal;

