import { useReducer, useState } from 'react';

const formReducer = (state, action) => {
  switch (action.type) {
    case 'UPDATE_FIELD':
      return {...state, [action.payload.field]: action.payload.value};
    case 'NEXT_STEP':
      return state;
    default:
      return state;
  }
};

const MultiStepForm = () => {
  const [step, setStep] = useState(1);
  const [formData, dispatch] = useReducer(formReducer, { firstName: '', lastName: '', email: '', address: '' });
  const [submitted, setSubmitted] = useState(false);

  const handleNext = () => {
    if (step < 3) setStep(step + 1);
  };

  const handlePrev = () => {
    if (step > 1) setStep(step - 1);
  };

  const handleSubmit = () => {
    setSubmitted(true);
  };

  if (submitted) {
    return <div style={{maxWidth: '500px', margin: '40px auto', textAlign: 'center'}}><h2>✓ Form Submitted!</h2></div>;
  }

  return (
    <div style={{maxWidth: '500px', margin: '40px auto', padding: '40px', background: 'white', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)'}}>
      <div style={{marginBottom: '30px'}}>
        <div style={{display: 'flex', gap: '10px'}}>
          {[1,2,3].map(i => (
            <div key={i} style={{
              width: '40px',
              height: '40px',
              borderRadius: '50%',
              background: i <= step ? '#007bff' : '#e5e7eb',
              color: 'white',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontWeight: '600'
            }}>{i}</div>
          ))}
        </div>
      </div>

      {step === 1 && (
        <div>
          <h3>Personal Information</h3>
          <input placeholder="First Name" value={formData.firstName} onChange={(e) => dispatch({type: 'UPDATE_FIELD', payload: {field: 'firstName', value: e.target.value}})} style={{width: '100%', padding: '10px', marginBottom: '10px', borderRadius: '4px', border: '1px solid #ddd'}} />
          <input placeholder="Last Name" value={formData.lastName} onChange={(e) => dispatch({type: 'UPDATE_FIELD', payload: {field: 'lastName', value: e.target.value}})} style={{width: '100%', padding: '10px', marginBottom: '10px', borderRadius: '4px', border: '1px solid #ddd'}} />
        </div>
      )}

      {step === 2 && (
        <div>
          <h3>Contact Information</h3>
          <input placeholder="Email" value={formData.email} onChange={(e) => dispatch({type: 'UPDATE_FIELD', payload: {field: 'email', value: e.target.value}})} style={{width: '100%', padding: '10px', marginBottom: '10px', borderRadius: '4px', border: '1px solid #ddd'}} />
        </div>
      )}

      {step === 3 && (
        <div>
          <h3>Address Information</h3>
          <input placeholder="Address" value={formData.address} onChange={(e) => dispatch({type: 'UPDATE_FIELD', payload: {field: 'address', value: e.target.value}})} style={{width: '100%', padding: '10px', marginBottom: '10px', borderRadius: '4px', border: '1px solid #ddd'}} />
          <div style={{background: '#f3f4f6', padding: '16px', borderRadius: '6px', marginTop: '20px'}}>
            <p><strong>Review:</strong></p>
            <p>Name: {formData.firstName} {formData.lastName}</p>
            <p>Email: {formData.email}</p>
            <p>Address: {formData.address}</p>
          </div>
        </div>
      )}

      <div style={{display: 'flex', gap: '12px', marginTop: '30px'}}>
        <button onClick={handlePrev} disabled={step === 1} style={{flex: 1, padding: '12px', background: '#e5e7eb', border: 'none', borderRadius: '6px', cursor: 'pointer'}}>Previous</button>
        {step < 3 ? (
          <button onClick={handleNext} style={{flex: 1, padding: '12px', background: '#007bff', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer'}}>Next</button>
        ) : (
          <button onClick={handleSubmit} style={{flex: 1, padding: '12px', background: '#28a745', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer'}}>Submit</button>
        )}
      </div>
    </div>
  );
};

export default MultiStepForm;

