solution.src/hooks/useInfiniteScroll.js:
import { useState, useCallback, useRef, useEffect } from 'react';

const useInfiniteScroll = (fetchFn, initialPageSize = 10) => {
  const [items, setItems] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [hasMore, setHasMore] = useState(true);
  const [page, setPage] = useState(1);
  const observerTarget = useRef(null);

  const loadMore = useCallback(async () => {
    if (isLoading || !hasMore) return;
    setIsLoading(true);
    setError(null);
    try {
      const newItems = await fetchFn(page, initialPageSize);
      if (newItems.length < initialPageSize) setHasMore(false);
      setItems(prev => [...prev, ...newItems]);
      setPage(prev => prev + 1);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  }, [page, isLoading, hasMore, fetchFn, initialPageSize]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      entries => {
        if (entries[0].isIntersecting && hasMore && !isLoading) loadMore();
      },
      { threshold: 0.1 }
    );
    if (observerTarget.current) observer.observe(observerTarget.current);
    return () => {
      if (observerTarget.current) observer.unobserve(observerTarget.current);
    };
  }, [loadMore, hasMore, isLoading]);

  return { items, isLoading, error, hasMore, observerTarget };
};

export default useInfiniteScroll;

