import { useState } from 'react';
import useInfiniteScroll from './hooks/useInfiniteScroll';

const mockFetch = async (page, pageSize) => {
  await new Promise(resolve => setTimeout(resolve, 500));
  const items = [];
  for (let i = 0; i < pageSize; i++) {
    items.push({
      id: (page - 1) * pageSize + i,
      title: `Item ${(page - 1) * pageSize + i + 1}`,
      description: `Description for item ${(page - 1) * pageSize + i + 1}`,
    });
  }
  return items;
};

function App() {
  const { items, isLoading, error, hasMore, observerTarget } = useInfiniteScroll(mockFetch, 10);

  return (
    <div style={{maxWidth: '600px', margin: '0 auto', padding: '20px'}}>
      <h1>Infinite Scroll List</h1>
      <div>
        {items.map(item => (
          <div key={item.id} style={{padding: '16px', marginBottom: '12px', background: 'white', borderRadius: '8px', border: '1px solid #e5e7eb'}}>
            <h3>{item.title}</h3>
            <p>{item.description}</p>
          </div>
        ))}
      </div>
      {isLoading && <div style={{textAlign: 'center', padding: '20px'}}>Loading...</div>}
      {error && <div style={{textAlign: 'center', padding: '20px', color: 'red'}}>Error: {error}</div>}
      {hasMore && <div ref={observerTarget} style={{padding: '20px', textAlign: 'center'}}>Scroll to load more</div>}
      {!hasMore && <div style={{padding: '20px', textAlign: 'center'}}>No more items</div>}
    </div>
  );
}

export default App;

