import { useState, useEffect, useRef, useCallback } from 'react';

const VirtualizedList = ({ items, itemHeight = 60, overscan = 5, renderItem }) => {
  const [visibleRange, setVisibleRange] = useState({ start: 0, end: overscan });
  const [scrollTop, setScrollTop] = useState(0);
  const containerRef = useRef();

  const totalHeight = items.length * itemHeight;
  const visibleHeight = containerRef.current?.clientHeight || 600;

  const handleScroll = useCallback((e) => {
    const scrollValue = e.target.scrollTop;
    setScrollTop(scrollValue);
    const startIndex = Math.max(0, Math.floor(scrollValue / itemHeight) - overscan);
    const endIndex = Math.min(items.length, Math.ceil((scrollValue + visibleHeight) / itemHeight) + overscan);
    setVisibleRange({ start: startIndex, end: endIndex });
  }, [items.length, itemHeight, overscan, visibleHeight]);

  const visibleItems = items.slice(visibleRange.start, visibleRange.end);
  const offsetY = visibleRange.start * itemHeight;

  return (
    <div
      ref={containerRef}
      onScroll={handleScroll}
      style={{ height: '600px', overflow: 'auto', position: 'relative', border: '1px solid #e5e7eb', borderRadius: '8px' }}
    >
      <div style={{ height: `${totalHeight}px`, position: 'relative' }}>
        <div style={{ transform: `translateY(${offsetY}px)`, position: 'absolute', top: 0, left: 0, right: 0 }}>
          {visibleItems.map((item, index) => (
            <div key={visibleRange.start + index} style={{ height: `${itemHeight}px`, overflow: 'hidden' }}>
              {renderItem(item, visibleRange.start + index)}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default VirtualizedList;

