import { useState, useEffect } from 'react';
import useDebounce from '../hooks/useDebounce';

const Autocomplete = ({ onSelect }) => {
  const [inputValue, setInputValue] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const debouncedValue = useDebounce(inputValue, 300);

  const mockFetch = async (query) => {
    await new Promise(resolve => setTimeout(resolve, 300));
    const items = [
      { id: 1, title: 'Apple', category: 'Fruits' },
      { id: 2, title: 'Apricot', category: 'Fruits' },
      { id: 3, title: 'Avocado', category: 'Fruits' },
      { id: 4, title: 'Artichoke', category: 'Vegetables' },
    ];
    return items.filter(item => item.title.toLowerCase().includes(query.toLowerCase()));
  };

  useEffect(() => {
    if (!debouncedValue.trim()) {
      setSuggestions([]);
      return;
    }
    setIsLoading(true);
    mockFetch(debouncedValue).then(results => {
      setSuggestions(results);
      setIsLoading(false);
    });
  }, [debouncedValue]);

  const handleSelect = (item) => {
    onSelect(item);
    setInputValue('');
    setSuggestions([]);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'ArrowDown') {
      setSelectedIndex(Math.min(selectedIndex + 1, suggestions.length - 1));
    } else if (e.key === 'ArrowUp') {
      setSelectedIndex(Math.max(selectedIndex - 1, -1));
    } else if (e.key === 'Enter' && selectedIndex >= 0) {
      handleSelect(suggestions[selectedIndex]);
    }
  };

  return (
    <div style={{position: 'relative'}}>
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Search..."
        style={{width: '100%', padding: '12px 16px', fontSize: '1rem', border: '2px solid #e5e7eb', borderRadius: '8px', outline: 'none'}}
      />
      {isLoading && <span style={{position: 'absolute', right: '16px', top: '50%', transform: 'translateY(-50%)', animation: 'spin 1s linear infinite'}}>⏳</span>}
      {suggestions.length > 0 && (
        <ul style={{position: 'absolute', top: '100%', left: 0, right: 0, marginTop: '8px', background: 'white', border: '1px solid #e5e7eb', borderRadius: '8px', maxHeight: '400px', overflow: 'auto', listStyle: 'none', padding: 0}}>
          {suggestions.map((item, index) => (
            <li
              key={item.id}
              onClick={() => handleSelect(item)}
              style={{
                padding: '12px 16px',
                background: index === selectedIndex ? '#f3f4f6' : 'white',
                cursor: 'pointer'
              }}
            >
              {item.title} <span style={{color: '#6b7280', fontSize: '0.85rem'}}>({item.category})</span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Autocomplete;

